import { StatusBar } from 'expo-status-bar';
import * as React from 'react';
import { StyleSheet, Text, View, SafeAreaView, Button, Alert, TextInput, Image} from 'react-native';
import { NavigationContainer, createNativeStackNavigator } from '@react-navigation/native';

const DemarrageScreen = (props)=>{
  const goToLogin = () => {
    props.navigation.push('Login');
  }

    return(
      <SafeAreaView style={styles.container}>

        <View>
          <Text style={styles.Title}>Smart Cage</Text>
          <StatusBar style="auto" />
        </View>

        <View style={styles.Logo}>
          <Image
            source={require('../assets/logo.jpg')}
          />
  
        </View>
      <View style={styles.BoutonConnexion}>
      <Button
          title="Connexion"
          onPress={goToLogin}
        />
      </View>
      </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FFF',
      alignItems: 'center',
    },
  
    Logo: {
      top:20,
    },
  
    Title: {
      marginTop:100,
      fontSize:25,
      fontFamily: 'serif',
      textAlign:'left',
    },
  
    BoutonConnexion: {
      marginTop:50,
      width:200,
      height:200
    },
  
    BoutonInscription: {
      flexDirection: "row",
      top: 30,
    },
  });

  export default DemarrageScreen;
